import React, { useState, useEffect } from "react";
import axios from "axios";
import EventList from "../components/EventList";
import EventForm from "../components/EventForm";
import "./Dashboard.css";

const Dashboard = () => {
    const [events, setEvents] = useState([]);
    const [filter, setFilter] = useState({ category: "", date: "" });

    useEffect(() => {
        const fetchEvents = async () => {
            try {
                const response = await axios.get("http://localhost:5000/api/events", { params: filter });
                setEvents(response.data);
            } catch (err) {
                console.error("Failed to fetch events:", err.response?.data?.error || "Unknown error");
                alert(`Failed to fetch events: ${err.response?.data?.error || "Unknown error"}`);
            }
        };
        fetchEvents();
    }, [filter]);

    let isGuest = false;
    try {
        const decodedToken = JSON.parse(atob(localStorage.getItem("token").split(".")[1]));
        isGuest = decodedToken.userId === "guest";
    } catch (error) {
        console.error("Error decoding token:", error);
    }

    return (
        <div className="dashboard-container">
            <div className="dashboard-content">
                <h1 className="dashboard-title">Event Dashboard</h1>
                <div className="filter-container">
                    <input
                        type="text"
                        placeholder="Filter by category"
                        value={filter.category}
                        onChange={(e) => setFilter({ ...filter, category: e.target.value })}
                        className="filter-input"
                    />
                    <input
                        type="date"
                        value={filter.date}
                        onChange={(e) => setFilter({ ...filter, date: e.target.value })}
                        className="filter-input"
                    />
                </div>
                <EventForm isGuest={isGuest} />
                <EventList events={events} />
            </div>
        </div>
    );
};

export default Dashboard;
