import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Register.css";

const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({}); // State for validation errors
  const navigate = useNavigate();

  const validateForm = () => {
    const errors = {};

    // Username validation
    if (!username.trim()) {
      errors.username = "Username is required.";
    } else if (username.length < 3) {
      errors.username = "Username must be at least 3 characters long.";
    }

    // Password validation
    if (!password.trim()) {
      errors.password = "Password is required.";
    } else if (password.length < 6) {
      errors.password = "Password must be at least 6 characters long.";
    }

    setErrors(errors);
    return Object.keys(errors).length === 0; // Return true if no errors
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    // Validate the form
    if (!validateForm()) {
      return; // Stop if validation fails
    }

    try {
      await axios.post("http://localhost:5000/api/auth/register", { username, password });
      alert("User registered successfully!");
      navigate("/login");
    } catch (err) {
      console.error("Registration failed:", err.response?.data?.error || "Unknown error");
      setErrors({ server: err.response?.data?.error || "Registration failed. Please try again." });
    }
  };

  return (
    <div className="register-container">
      <div className="register-box">
        <h1 className="register-title">Register</h1>

        {/* Display server error message */}
        {errors.server && <p className="error-message">{errors.server}</p>}

        <form onSubmit={handleRegister} className="register-form" noValidate>
          <div className="form-group">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => {
                setUsername(e.target.value);
                setErrors({ ...errors, username: "" }); // Clear username error when typing
              }}
              className={`register-input ${errors.username ? "input-error" : ""}`}
              required
            />
            {errors.username && <p className="error-message">{errors.username}</p>}
          </div>

          <div className="form-group">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => {
                setPassword(e.target.value);
                setErrors({ ...errors, password: "" }); // Clear password error when typing
              }}
              className={`register-input ${errors.password ? "input-error" : ""}`}
              required
            />
            {errors.password && <p className="error-message">{errors.password}</p>}
          </div>

          <button type="submit" className="register-button">
            Register
          </button>
        </form>
      </div>
    </div>
  );
};

export default Register;
