import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";
import EventList from "../components/EventList";
import EventForm from "../components/EventForm";
import "./Dashboard.css";

const Dashboard = () => {
  const [events, setEvents] = useState({ upcomingEvents: [], pastEvents: [] });
  const [filter, setFilter] = useState({ category: "", date: "" });
  const [userId, setUserId] = useState(null);
  const [isGuest, setIsGuest] = useState(false);
  const [editingEvent, setEditingEvent] = useState(null);
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    fetchEvents();

    // Fetch User Details
    const token = localStorage.getItem("token");
    if (token) {
      const decodedToken = JSON.parse(atob(token.split(".")[1]));
      setUserId(decodedToken.userId);
      setIsGuest(decodedToken.userId === "guest");
    }

    // Initialize WebSocket Connection
    const newSocket = io("http://localhost:5000", {
      transports: ["websocket", "polling"],
      reconnectionAttempts: 5,
      reconnectionDelay: 3000,
    });

    newSocket.on("connect", () => {
      console.log("Connected to WebSocket server");
    });

    newSocket.on("newEvent", (newEvent) => {
      setEvents((prevEvents) => ({
        ...prevEvents,
        upcomingEvents: [...prevEvents.upcomingEvents, newEvent],
      }));
    });

    newSocket.on("updateEvent", (updatedEvent) => {
      setEvents((prevEvents) => ({
        ...prevEvents,
        upcomingEvents: prevEvents.upcomingEvents.map((event) =>
          event._id === updatedEvent._id ? updatedEvent : event
        ),
      }));
    });

    newSocket.on("cancelEvent", (cancelledEvent) => {
      setEvents((prevEvents) => ({
        ...prevEvents,
        upcomingEvents: prevEvents.upcomingEvents.filter(
          (event) => event._id !== cancelledEvent._id
        ),
      }));
    });

    setSocket(newSocket);

    return () => {
      newSocket.disconnect();
      console.log("Disconnected from WebSocket server");
    };
  }, [filter]);

  const fetchEvents = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to view events.");
        return;
      }
      const response = await axios.get("http://localhost:5000/api/events", {
        params: filter,
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log("Fetched events:", response.data);
      setEvents(response.data);
    } catch (err) {
      console.error("Failed to fetch events:", err.response?.data?.error || "Unknown error");
      alert(`Failed to fetch events: ${err.response?.data?.error || "Unknown error"}`);
    }
  };

  const handleAttendEvent = async (eventId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to attend an event.");
        return;
      }
      await axios.post(`http://localhost:5000/api/events/${eventId}/attend`, null, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchEvents();
    } catch (err) {
      console.error("Failed to attend event:", err.response?.data?.error || "Unknown error");
      alert(`Failed to attend event: ${err.response?.data?.error || "Unknown error"}`);
    }
  };

  const handleLeaveEvent = async (eventId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to leave an event.");
        return;
      }
      await axios.post(`http://localhost:5000/api/events/${eventId}/leave`, null, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchEvents();
    } catch (err) {
      console.error("Failed to leave event:", err.response?.data?.error || "Unknown error");
      alert(`Failed to leave event: ${err.response?.data?.error || "Unknown error"}`);
    }
  };

  const handleCancelEvent = async (eventId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to cancel an event.");
        return;
      }
      await axios.post(`http://localhost:5000/api/events/${eventId}/cancel`, null, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchEvents();
    } catch (err) {
      console.error("Failed to cancel event:", err.response?.data?.error || "Unknown error");
      alert(`Failed to cancel event: ${err.response?.data?.error || "Unknown error"}`);
    }
  };

  const handleUpdateEvent = (event) => {
    setEditingEvent(event);
  };

  const handleSaveEvent = async (updatedEvent) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to update an event.");
        return;
      }
      await axios.put(`http://localhost:5000/api/events/${updatedEvent._id}`, updatedEvent, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchEvents();
      setEditingEvent(null);
    } catch (err) {
      console.error("Failed to update event:", err.response?.data?.error || "Unknown error");
      alert(`Failed to update event: ${err.response?.data?.error || "Unknown error"}`);
    }
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-content">
        <h1 className="dashboard-title">Event Dashboard</h1>

        <div className="filter-container">
          <select
            name="category"
            value={filter.category}
            onChange={(e) => setFilter({ ...filter, category: e.target.value })}
            className="filter-input"
          >
            <option value="">All Categories</option>
            <option value="Business">Business</option>
            <option value="Social">Social</option>
            <option value="Education">Education</option>
          </select>

          <input
            type="date"
            name="date"
            value={filter.date}
            onChange={(e) => setFilter({ ...filter, date: e.target.value })}
            className="filter-input"
          />

          <button onClick={fetchEvents} className="search-button">Search</button>
        </div>

        <EventForm isGuest={isGuest} />

        {editingEvent && (
          <div className="edit-event-form">
            <h2>Edit Event</h2>
            <form onSubmit={(e) => { e.preventDefault(); handleSaveEvent(editingEvent); }}>
              <input type="text" value={editingEvent.name} className="form-input" required />
              <button type="submit" className="submit-button">Save</button>
              <button type="button" className="cancel-button" onClick={() => setEditingEvent(null)}>Cancel</button>
            </form>
          </div>
        )}

        <EventList events={events.upcomingEvents} title="Upcoming Events" onAttend={handleAttendEvent} />
        <EventList events={events.pastEvents} title="Past Events" onLeave={handleLeaveEvent} />
      </div>
    </div>
  );
};

export default Dashboard;
