import React from "react";
import "./EventList.css";

const EventList = ({ events, title, onAttend, onLeave, onUpdate, onDelete, onCancel, isPast }) => {
  const safeEvents = Array.isArray(events) ? events : [];

  return (
    <div className="event-list">
      <h2>{title}</h2>
      <table className="event-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Date</th>
            <th>Category</th>
            <th>Created By</th>
            <th>Attendees</th>
            <th>Status</th>
            {!isPast && <th>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {safeEvents.map((event) => (
            <tr key={event._id}>
              <td>{event.name}</td>
              <td>{event.description}</td>
              <td>{new Date(event.date).toLocaleDateString()}</td>
              <td>{event.category}</td>
              <td>{event.createdBy?.username || "Unknown"}</td>
              <td>{event.attendees?.length || 0}</td>
              <td>{event.status}</td>
              {!isPast && (
                <td>
                  <button onClick={() => onUpdate(event._id)}>Update</button>
                  <button onClick={() => onDelete(event._id)}>Delete</button>
                  <button onClick={() => onCancel(event._id)}>Cancel</button>
                </td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EventList;
