import React, { useState } from "react";
import axios from "axios";
import "./EventForm.css"; // Import CSS file

const EventForm = ({ isGuest }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [date, setDate] = useState("");
  const [category, setCategory] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:5000/api/events",
        { name, description, date, category },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Event created successfully!");
    } catch (err) {
      console.error("Failed to create event:", err);
    }
  };

  if (isGuest) {
    return <p className="guest-message">Guest users cannot create events.</p>;
  }

  return (
    <form onSubmit={handleSubmit} className="event-form">
      <h2 className="event-form-title">Create Event</h2>
      <div className="form-group">
        <input
          type="text"
          placeholder="Event Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="form-input"
        />
      </div>
      <div className="form-group">
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="form-textarea"
        />
      </div>
      <div className="form-group">
        <input
          type="datetime-local"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="form-input"
        />
      </div>
      <div className="form-group">
        <input
          type="text"
          placeholder="Category"
          value={category}
          onChange={(e) => setCategory(e.target.value)}
          className="form-input"
        />
      </div>
      <button type="submit" className="submit-button">
        Create Event
      </button>
    </form>
  );
};

export default EventForm;
