import React from "react";
import "./EventList.css";

const EventList = ({ events, title, onAttend, onLeave, onUpdate, onCancel, isPast, userId, isGuest }) => {
  const safeEvents = Array.isArray(events) ? events : [];

  return (
    <div className="event-list">
      <h2>{title}</h2>
      <table className="event-table">
        <thead>
          <tr>
            <th>Status</th>
            <th>Name</th>
            <th>Description</th>
            <th>Date & Time</th>
            <th>Category</th>
            <th>Created By</th>
            <th>Attendees</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {safeEvents.map((event) => {
            const eventDateTime = new Date(event.date);
            const isEventPast = eventDateTime <= new Date();
            const isEventCancelled = event.status === "cancelled";

            // Convert UTC to Local Time for display
            const formattedEventDateTime = eventDateTime.toLocaleString("en-US", {
              year: "numeric",
              month: "long",
              day: "numeric",
              hour: "2-digit",
              minute: "2-digit",
              second: "2-digit",
              hour12: true,
              timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone,
            });

            return (
              <tr key={event._id}>
                <td>
                  {isEventCancelled ? (
                    <span className="cancelled-message">Event Cancelled</span>
                  ) : isEventPast ? (
                    <span className="completed-message">Event Completed</span>
                  ) : (
                    "Active"
                  )}
                </td>
                <td>{event.name}</td>
                <td>{event.description}</td>
                <td>{formattedEventDateTime}</td>
                <td>{event.category}</td>
                <td>{event.createdBy?.username || "Unknown"}</td>
                <td>{event.attendees?.length || 0}</td>
                <td>
                  {!isGuest && event.createdBy === userId && (
                    <>
                      <button onClick={() => onUpdate(event)}>Edit</button>
                      <button onClick={() => onCancel(event._id)}>Cancel</button>
                    </>
                  )}
                  {!isGuest &&
                    event.createdBy !== userId &&
                    event.status !== "cancelled" &&
                    !isEventPast && (
                      <>
                        <button onClick={() => onAttend(event._id)}>Attend</button>
                        <button onClick={() => onLeave(event._id)}>Leave</button>
                        <button onClick={() => onCancel(event._id)}>Cancel</button>
                      </>
                    )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default EventList;
