import React, { useState, useEffect } from "react";
import axios from "axios";
import io from "socket.io-client";
import EventList from "../components/EventList";
import EventForm from "../components/EventForm";
import "./Dashboard.css";

const Dashboard = () => {
  const [events, setEvents] = useState({ upcomingEvents: [], pastEvents: [] });
  const [filter, setFilter] = useState({ category: "", date: "" });
  const [userId, setUserId] = useState(null);
  const [isGuest, setIsGuest] = useState(false);
  const [editingEvent, setEditingEvent] = useState(null); // Track the event being edited

  const fetchEvents = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to view events.");
        return;
      }
      const response = await axios.get("http://localhost:5000", {
        params: filter,
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setEvents(response.data);
    } catch (err) {
      console.error(
        "Failed to fetch events:",
        err.response?.data?.error || "Unknown error"
      );
      alert(
        `Failed to fetch events: ${
          err.response?.data?.error || "Unknown error"
        }`
      );
    }
  };

  useEffect(() => {
    fetchEvents();
    const token = localStorage.getItem("token");
    if (token) {
      const decodedToken = JSON.parse(atob(token.split(".")[1]));
      setUserId(decodedToken.userId);
      setIsGuest(decodedToken.userId === "guest");
    }
  }, [filter]); // Refetch events when filters change

  // Connect to the Socket.IO server
  const socket = io("http://localhost:5000/api/events/", {
    transports: ["websocket"], // Force WebSocket transport
  });

  useEffect(() => {
    socket.on("newEvent", (newEvent) => {
      setEvents((prevEvents) => {
        const now = new Date();
        const upcomingEvents = [...prevEvents.upcomingEvents];
        const pastEvents = [...prevEvents.pastEvents];
        if (new Date(newEvent.date) > now) {
          upcomingEvents.push(newEvent);
        } else {
          pastEvents.push(newEvent);
        }
        return { upcomingEvents, pastEvents };
      });
    });

    socket.on("updateEvent", (updatedEvent) => {
      setEvents((prevEvents) => {
        const updatedEvents = prevEvents.upcomingEvents.map((event) =>
          event._id === updatedEvent._id ? updatedEvent : event
        );
        return { ...prevEvents, upcomingEvents: updatedEvents };
      });
    });

    socket.on("cancelEvent", (cancelledEvent) => {
      setEvents((prevEvents) => {
        const updatedEvents = prevEvents.upcomingEvents.map((event) =>
          event._id === cancelledEvent._id ? cancelledEvent : event
        );
        return { ...prevEvents, upcomingEvents: updatedEvents };
      });
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  // Handle attending an event
  const handleAttendEvent = async (eventId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to attend an event.");
        return;
      }
      await axios.post(
        `http://localhost:5000/api/events/${eventId}/attend`,
        null,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      fetchEvents(); // Refresh events after attending
    } catch (err) {
      console.error(
        "Failed to attend event:",
        err.response?.data?.error || "Unknown error"
      );
      alert(
        `Failed to attend event: ${
          err.response?.data?.error || "Unknown error"
        }`
      );
    }
  };

  // Handle leaving an event
  const handleLeaveEvent = async (eventId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to leave an event.");
        return;
      }
      await axios.post(
        `http://localhost:5000/api/events/${eventId}/leave`,
        null,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      fetchEvents(); // Refresh events after leaving
    } catch (err) {
      console.error(
        "Failed to leave event:",
        err.response?.data?.error || "Unknown error"
      );
      alert(
        `Failed to leave event: ${
          err.response?.data?.error || "Unknown error"
        }`
      );
    }
  };

  // Handle cancelling an event
  const handleCancelEvent = async (eventId) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to cancel an event.");
        return;
      }
      await axios.post(
        `http://localhost:5000/api/events/${eventId}/cancel`,
        null,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      fetchEvents(); // Refresh events after cancellation
    } catch (err) {
      console.error(
        "Failed to cancel event:",
        err.response?.data?.error || "Unknown error"
      );
      alert(
        `Failed to cancel event: ${
          err.response?.data?.error || "Unknown error"
        }`
      );
    }
  };

  // Handle updating an event
  const handleUpdateEvent = (event) => {
    setEditingEvent(event); // Set the event to be edited
  };

  // Handle saving an updated event
  const handleSaveEvent = async (updatedEvent) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to update an event.");
        return;
      }
      await axios.put(
        `http://localhost:5000/api/events/${updatedEvent._id}`,
        updatedEvent,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      fetchEvents(); // Refresh events after updating
      setEditingEvent(null); // Close the edit form
    } catch (err) {
      console.error(
        "Failed to update event:",
        err.response?.data?.error || "Unknown error"
      );
      alert(
        `Failed to update event: ${
          err.response?.data?.error || "Unknown error"
        }`
      );
    }
  };

  return (
    <div>
      {/* Filter Form */}
      <input
        type="text"
        placeholder="Category"
        onChange={(e) => setFilter({ ...filter, category: e.target.value })}
        className="filter-input"
      />
      <select
        onChange={(e) => setFilter({ ...filter, date: e.target.value })}
        className="filter-input"
      >
        <option value="">All Categories</option>
        <option value="Business & Corporate Events">
          Business & Corporate Events
        </option>
        <option value="Social & Personal Events">Social & Personal Events</option>
        <option value="Educational Events">Educational Events</option>
        <option value="Cultural & Religious Events">
          Cultural & Religious Events
        </option>
        <option value="Entertainment & Recreational Events">
          Entertainment & Recreational Events
        </option>
        <option value="Sports & Fitness Events">Sports & Fitness Events</option>
        <option value="Charity & Fundraising Events">
          Charity & Fundraising Events
        </option>
        <option value="Government & Political Events">
          Government & Political Events
        </option>
        <option value="Technology & Innovation Events">
          Technology & Innovation Events
        </option>
        <option value="Virtual & Hybrid Events">Virtual & Hybrid Events</option>
      </select>
      <button onClick={fetchEvents}>Search</button>

      {editingEvent && (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleSaveEvent(editingEvent);
          }}
        >
          <input
            type="text"
            value={editingEvent.name}
            onChange={(e) =>
              setEditingEvent({ ...editingEvent, name: e.target.value })
            }
            className="form-input"
            required
          />
          <textarea
            value={editingEvent.description}
            onChange={(e) =>
              setEditingEvent({ ...editingEvent, description: e.target.value })
            }
            className="form-textarea"
            required
          />
          <input
            type="date"
            value={editingEvent.date}
            onChange={(e) =>
              setEditingEvent({ ...editingEvent, date: e.target.value })
            }
            className="form-input"
            required
          />
          <select
            value={editingEvent.category}
            onChange={(e) =>
              setEditingEvent({ ...editingEvent, category: e.target.value })
            }
            className="form-input"
            required
          >
            <option value="">Select Category</option>
            <option value="Business & Corporate Events">
              Business & Corporate Events
            </option>
            <option value="Social & Personal Events">
              Social & Personal Events
            </option>
            <option value="Educational Events">Educational Events</option>
            <option value="Cultural & Religious Events">
              Cultural & Religious Events
            </option>
            <option value="Entertainment & Recreational Events">
              Entertainment & Recreational Events
            </option>
            <option value="Sports & Fitness Events">
              Sports & Fitness Events
            </option>
            <option value="Charity & Fundraising Events">
              Charity & Fundraising Events
            </option>
            <option value="Government & Political Events">
              Government & Political Events
            </option>
            <option value="Technology & Innovation Events">
              Technology & Innovation Events
            </option>
            <option value="Virtual & Hybrid Events">
              Virtual & Hybrid Events
            </option>
          </select>
          <button type="submit">Save Changes</button>
          <button onClick={() => setEditingEvent(null)}>Cancel</button>
        </form>
      )}
    </div>
  );
};

export default Dashboard;
