import React from "react";
import "./EventList.css";

const EventList = ({ events, title, onUpdate, onCancel, userId, isGuest }) => {
    const safeEvents = Array.isArray(events) ? events : [];

    return (
        <div className="event-list">
            <h2>{title}</h2>
            <table className="event-table">
                <thead>
                    <tr>
                        <th>Status</th>
                        <th>Name</th>
                        <th>Description</th>
                        <th>Date</th>
                        <th>Category</th>
                        <th>Created By</th>
                        <th>Attendees</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {safeEvents.map((event) => (
                        <tr key={event._id}>
                            <td>
                                {event.status === "cancelled" ? (
                                    <span className="cancelled-message">Event Cancelled</span>
                                ) : (
                                    "Active"
                                )}
                            </td>
                            <td>{event.name}</td>
                            <td>{event.description}</td>
                            <td>{new Date(event.date).toLocaleDateString()}</td>
                            <td>{event.category}</td>
                            <td>{event.createdBy?.username || "Unknown"}</td>
                            <td>{event.attendees?.length || 0}</td>
                            <td>
                                {/* Update and Cancel buttons for the event creator */}
                                {!isGuest && event.createdBy === userId && (
                                    <>
                                        <button onClick={() => onUpdate(event)}>Edit</button>
                                        <button onClick={() => onCancel(event._id)}>Cancel</button>
                                    </>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default EventList;
