import React, { useState } from "react";
import axios from "axios";

const EventForm = ({ isGuest, onEventCreated }) => {
  const [eventName, setEventName] = useState("");
  const [eventDescription, setEventDescription] = useState("");
  const [eventDate, setEventDate] = useState("");
  const [eventCategory, setEventCategory] = useState("");
  const [errors, setErrors] = useState({});

  // Validation logic
  const validateForm = () => {
    let errors = {};
    if (!eventName.trim()) {
      errors.eventName = "Event name is required.";
    } else if (eventName.length < 3) {
      errors.eventName = "Event name must be at least 3 characters.";
    }
    if (!eventDescription.trim()) {
      errors.eventDescription = "Description is required.";
    } else if (eventDescription.length < 10) {
      errors.eventDescription = "Description must be at least 10 characters.";
    }
    if (!eventDate) {
      errors.eventDate = "Date and time are required.";
    } else {
      const selectedDate = new Date(eventDate);
      const currentDate = new Date();
      if (selectedDate <= currentDate) {
        errors.eventDate = "Please select a future date and time.";
      }
    }
    if (!eventCategory) {
      errors.eventCategory = "Please select a category.";
    }
    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You need to log in to create an event.");
        return;
      }

      const response = await axios.post(
        "https://event-management-app-trmh.onrender.com/api/events",
        { name: eventName, description: eventDescription, date: eventDate, category: eventCategory },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Reset form fields and errors
      setEventName("");
      setEventDescription("");
      setEventDate("");
      setEventCategory("");
      setErrors({});
      alert("Event created successfully!");

      // Notify parent component if callback is provided
      if (onEventCreated) {
        onEventCreated(response.data);
      }
    } catch (err) {
      console.error("Failed to create event:", err.response?.data?.error || "Unknown error");
      alert(`Failed to create event: ${err.response?.data?.error || "Unknown error"}`);
    }
  };

  // Conditional rendering for guest users
  if (isGuest) {
    return <p className="guest-message">Guest users cannot create events.</p>;
  }

  return (
    <div className="event-form">
      <h2>Create Event</h2>
      <form onSubmit={handleSubmit}>
        {/* Event Name Field */}
        <div>
          <input
            type="text"
            placeholder="Event Name"
            value={eventName}
            onChange={(e) => {
              setEventName(e.target.value);
              setErrors({ ...errors, eventName: "" });
            }}
            className={errors.eventName ? "input-error" : ""}
          />
          {errors.eventName && <p className="error-message">{errors.eventName}</p>}
        </div>

        {/* Description Field */}
        <div>
          <textarea
            placeholder="Description"
            value={eventDescription}
            onChange={(e) => {
              setEventDescription(e.target.value);
              setErrors({ ...errors, eventDescription: "" });
            }}
            className={errors.eventDescription ? "input-error" : ""}
          />
          {errors.eventDescription && <p className="error-message">{errors.eventDescription}</p>}
        </div>

        {/* Date Field */}
        <div>
          <input
            type="datetime-local"
            value={eventDate}
            onChange={(e) => {
              setEventDate(e.target.value);
              setErrors({ ...errors, eventDate: "" });
            }}
            className={errors.eventDate ? "input-error" : ""}
            min={new Date().toISOString().slice(0, 16)}
          />
          {errors.eventDate && <p className="error-message">{errors.eventDate}</p>}
        </div>

        {/* Category Field */}
        <div>
          <select
            value={eventCategory}
            onChange={(e) => {
              setEventCategory(e.target.value);
              setErrors({ ...errors, eventCategory: "" });
            }}
            className={errors.eventCategory ? "input-error" : ""}
          >
            <option value="">Select Category</option>
            <option value="Business & Corporate Events">Business & Corporate Events</option>
            <option value="Social & Personal Events">Social & Personal Events</option>
            <option value="Educational Events">Educational Events</option>
            <option value="Cultural & Religious Events">Cultural & Religious Events</option>
            <option value="Entertainment & Recreational Events">Entertainment & Recreational Events</option>
            <option value="Sports & Fitness Events">Sports & Fitness Events</option>
            <option value="Charity & Fundraising Events">Charity & Fundraising Events</option>
            <option value="Government & Political Events">Government & Political Events</option>
            <option value="Technology & Innovation Events">Technology & Innovation Events</option>
            <option value="Virtual & Hybrid Events">Virtual & Hybrid Events</option>
          </select>
          {errors.eventCategory && <p className="error-message">{errors.eventCategory}</p>}
        </div>

        {/* Submit Button */}
        <button type="submit">Create Event</button>
      </form>
    </div>
  );
};

export default EventForm;
