import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // State for error messages
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    // Form validation
    if (!username || !password) {
      setError("Please fill in all fields.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/auth/login", { username, password });
      localStorage.setItem("token", response.data.token);
      navigate("/dashboard");
    } catch (err) {
      console.error("Login failed:", err.response?.data?.error || "Unknown error");
      setError(err.response?.data?.error || "Login failed. Please try again.");
    }
  };

  const handleGuestLogin = async () => {
    try {
      const response = await axios.post("http://localhost:5000/api/auth/guest-login");
      localStorage.setItem("token", response.data.token);
      navigate("/dashboard");
    } catch (err) {
      console.error("Guest login failed:", err.response?.data?.error || "Unknown error");
      setError(err.response?.data?.error || "Guest login failed. Please try again.");
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1 className="login-title">Login</h1>

        {/* Display error message */}
        {error && <p className="error-message">{error}</p>}

        <form onSubmit={handleLogin} className="login-form">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => {
              setUsername(e.target.value);
              setError(""); // Clear error when user types
            }}
            className="login-input"
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => {
              setPassword(e.target.value);
              setError(""); // Clear error when user types
            }}
            className="login-input"
            required
          />
          <button type="submit" className="login-button">
            Login
          </button>
        </form>

        <button onClick={handleGuestLogin} className="guest-login-button">
          Login as Guest
        </button>
      </div>
    </div>
  );
};

export default Login;
