import React, { useState } from "react";
import axios from "axios";

const EventForm = ({ onEventCreated, editingEvent, onSaveEvent }) => {
  const [formData, setFormData] = useState({
    name: editingEvent?.name || "",
    description: editingEvent?.description || "",
    date: editingEvent?.date || "",
    category: editingEvent?.category || "",
    image: null, // To store the uploaded image file
  });

  const [imagePreview, setImagePreview] = useState(editingEvent?.image || "");

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle image upload
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Create a preview of the image
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result);
    };
    reader.readAsDataURL(file);

    // Upload image to Cloudinary
    const formData = new FormData();
    formData.append("file", file);
    formData.append("upload_preset", "your_upload_preset"); // Replace with your Cloudinary upload preset

    try {
      const response = await axios.post(
        "https://api.cloudinary.com/v1_1/your_cloud_name/image/upload", // Replace with your Cloudinary cloud name
        formData
      );
      const imageUrl = response.data.secure_url; // Get the uploaded image URL
      setFormData({ ...formData, image: imageUrl }); // Save the image URL to form data
    } catch (error) {
      console.error("Error uploading image to Cloudinary:", error);
      alert("Failed to upload image. Please try again.");
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (editingEvent) {
      // Update existing event
      onSaveEvent({ ...formData, _id: editingEvent._id });
    } else {
      // Create new event
      onEventCreated(formData);
    }

    // Reset form
    setFormData({
      name: "",
      description: "",
      date: "",
      category: "",
      image: null,
    });
    setImagePreview("");
  };

  return (
    <form onSubmit={handleSubmit} className="event-form">
      <h2>{editingEvent ? "Edit Event" : "Create New Event"}</h2>

      {/* Image Upload */}
      <div className="form-group">
        <label>Upload Image</label>
        <input type="file" accept="image/*" onChange={handleImageUpload} />
        {imagePreview && <img src={imagePreview} alt="Preview" className="image-preview" />}
      </div>

      {/* Event Name */}
      <div className="form-group">
        <label>Name</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>

      {/* Event Description */}
      <div className="form-group">
        <label>Description</label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          required
        />
      </div>

      {/* Event Date */}
      <div className="form-group">
        <label>Date</label>
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
        />
      </div>

      {/* Event Category */}
      <div className="form-group">
        <label>Category</label>
        <select
          name="category"
          value={formData.category}
          onChange={handleChange}
          required
        >
          <option value="">Select Category</option>
          <option value="Business & Corporate Events">Business & Corporate Events</option>
          <option value="Social & Personal Events">Social & Personal Events</option>
          <option value="Educational Events">Educational Events</option>
          <option value="Cultural & Religious Events">Cultural & Religious Events</option>
          <option value="Entertainment & Recreational Events">Entertainment & Recreational Events</option>
          <option value="Sports & Fitness Events">Sports & Fitness Events</option>
          <option value="Charity & Fundraising Events">Charity & Fundraising Events</option>
          <option value="Government & Political Events">Government & Political Events</option>
          <option value="Technology & Innovation Events">Technology & Innovation Events</option>
          <option value="Virtual & Hybrid Events">Virtual & Hybrid Events</option>
        </select>
      </div>

      {/* Submit Button */}
      <button type="submit">{editingEvent ? "Save Changes" : "Create Event"}</button>
    </form>
  );
};

export default EventForm;
