import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({}); // State for validation errors
  const navigate = useNavigate();

  const validateForm = () => {
    const errors = {};

    // Username validation
    if (!username.trim()) {
      errors.username = "Username is required.";
    } else if (username.length < 3) {
      errors.username = "Username must be at least 3 characters long.";
    }

    // Password validation
    if (!password.trim()) {
      errors.password = "Password is required.";
    } else if (password.length < 6) {
      errors.password = "Password must be at least 6 characters long.";
    }

    setErrors(errors);
    return Object.keys(errors).length === 0; // Return true if no errors
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    // Validate the form
    if (!validateForm()) {
      return; // Stop if validation fails
    }

    try {
      const response = await axios.post("https://event-management-app-o5ox.onrender.com/api/auth/login", { username, password });
      localStorage.setItem("token", response.data.token);
      navigate("/dashboard");
    } catch (err) {
      console.error("Login failed:", err.response?.data?.error || "Unknown error");
      setErrors({ server: err.response?.data?.error || "Login failed. Please try again." });
    }
  };

  const handleGuestLogin = async () => {
    try {
      const response = await axios.post("https://event-management-app-o5ox.onrender.com/api/auth/guest-login");
      localStorage.setItem("token", response.data.token);
      navigate("/dashboard");
    } catch (err) {
      console.error("Guest login failed:", err.response?.data?.error || "Unknown error");
      setErrors({ server: err.response?.data?.error || "Guest login failed. Please try again." });
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1 className="login-title">Login</h1>

        {/* Display server error message */}
        {errors.server && <p className="error-message">{errors.server}</p>}

        <form onSubmit={handleLogin} className="login-form" noValidate>
          <div className="form-group">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => {
                setUsername(e.target.value);
                setErrors({ ...errors, username: "" }); // Clear username error when typing
              }}
              className={`login-input ${errors.username ? "input-error" : ""}`}
              required
            />
            {errors.username && <p className="error-message">{errors.username}</p>}
          </div>

          <div className="form-group">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => {
                setPassword(e.target.value);
                setErrors({ ...errors, password: "" }); // Clear password error when typing
              }}
              className={`login-input ${errors.password ? "input-error" : ""}`}
              required
            />
            {errors.password && <p className="error-message">{errors.password}</p>}
          </div>

          <button type="submit" className="login-button">
            Login
          </button>
        </form>

        <button onClick={handleGuestLogin} className="guest-login-button">
          Login as Guest
        </button>
      </div>
    </div>
  );
};

export default Login;
